INSERT INTO Sach (TenSach, GiaBan, MoTa, AnhBia, NgayCapNhat, SoLuongTon, MaNXB, MaChuDe, MaTacGia) VALUES (N'Những người khốn khổ', '105000', N'Những người khốn khổ là tiểu thuyết của văn hào Pháp 1, được xuất bản năm 1862. Tác phẩm được đánh giá là một trong những tiểu thuyết nổi tiếng nhất của nền văn học thế giới thế kỷ 19.', 'nhungnguoikhonkho.jpg', '2022-10-24', 1000, 1, 2, 1);
INSERT INTO Sach (TenSach, GiaBan, MoTa, AnhBia, NgayCapNhat, SoLuongTon, MaNXB, MaChuDe, MaTacGia) VALUES (N'Nhà thờ Đức Bà Paris', '105000', N'Nhà thờ Đức Bà Paris là tiểu thuyết của văn hào Pháp 1. Tác phẩm ra đời xuất phát từ việc tác giả muốn viết một cuốn tiểu thuyết về ngôi nhà thờ nổi tiếng ở thủ đô Paris vào năm 1828', 'nhathoducba.jpg', '2022-10-24', 1200, 2, 2, 1);
INSERT INTO Sach (TenSach, GiaBan, MoTa, AnhBia, NgayCapNhat, SoLuongTon, MaNXB, MaChuDe, MaTacGia) VALUES (N'Chiêm Ngưỡng', '105000', N'Được dịch từ tiếng Anh-Les Contemplations là một tuyển tập thơ của 1, xuất bản năm 1856. Nó bao gồm 156 bài thơ trong sáu cuốn sách. Hầu hết các bài thơ được viết từ năm 1841 đến năm 1855, mặc dù có niên đại lâu đời nhất từ ​​năm 1830.', 'chiemnguong.jpg', '2022-10-24', 900, 2, 2, 1);
INSERT INTO Sach (TenSach, GiaBan, MoTa, AnhBia, NgayCapNhat, SoLuongTon, MaNXB, MaChuDe, MaTacGia) VALUES (N'“Ngày Cuối Cùng Của Một Tử Tù', '105000', N'Ngày cuối cùng của một người đàn ông bị kết án là một tiểu thuyết của 1 được xuất bản lần đầu tiên vào năm 1829. Nó kể lại những suy nghĩ của một người đàn ông bị kết án tử hình. 1 viết cuốn tiểu thuyết này để bày tỏ cảm xúc của mình rằng án tử hình nên được bãi bỏ', 'ngaycuoitutu.jpg', '2022-10-24', 850, 2, 1, 2);
INSERT INTO Sach (TenSach, GiaBan, MoTa, AnhBia, NgayCapNhat, SoLuongTon, MaNXB, MaChuDe, MaTacGia) VALUES (N'Người cười', '105000', N'The Man Who Laughs là một tiểu thuyết của 1, được xuất bản lần đầu vào tháng 4 năm 1869 với tựa đề tiếng Pháp là Homme qui rit. Nó diễn ra ở Anh bắt đầu từ năm 1690 và kéo dài đến đầu thế kỷ 18 trị vì của Nữ hoàng Anne.', 'nguoicuoi.jpg', '2022-10-24', '600', 1, 2, 3);
INSERT INTO Sach (TenSach, GiaBan, MoTa, AnhBia, NgayCapNhat, SoLuongTon, MaNXB, MaChuDe, MaTacGia) VALUES (N'Dế Mèn phiêu lưu ký', '105000', N'"Dế Mèn phiêu lưu ký là tác phẩm văn xuôi đặc sắc và nổi tiếng nhất của nhà văn Tô Hoài viết về loài vật, dành cho lứa tuổi thiếu nhi. Ban đầu truyện có tên là ""Con dế mèn"" do Nhà xuất bản Tân Dân, Hà Nội phát hành năm 1941. Sau đó, được sự ủng hộ nhiệt tình của nhân dân, Tô Hoài viết thêm truyện ""Dế Mèn phiêu lưu ký""."', 'demen.jpg', '2022-10-24', 420, 1, 3, 2);
INSERT INTO Sach (TenSach, GiaBan, MoTa, AnhBia, NgayCapNhat, SoLuongTon, MaNXB, MaChuDe, MaTacGia) VALUES (N'Quê người', '105000', N'Trong làng văn học Việt Nam rất hiếm có nhà văn nào nhận được sự yêu thương, mến mộ từ nhiều thế hệ độc giả khác nhau như nhà văn Tô Hoài. Nếu các bạn nhỏ biết đến ông như một người bạn dí dỏm, đáng yêu qua kiệt tác Dế mèn phiêu lưu ký và series truyện ngộ nghĩnh về loài vật, thì độc giả lớn tuổi biết đến ông như một cây đại thụ của làng văn với nhiều tác phẩm đã trở thành những tượng đài bất tử như Vợ chồng A Phủ, Cứu đất cứu mường, Chuyện đầm sen đền Đồng Cổ…', 'quenguoi.jpg', '2022-10-24', 360, 1, 1, 2);
INSERT INTO Sach (TenSach, GiaBan, MoTa, AnhBia, NgayCapNhat, SoLuongTon, MaNXB, MaChuDe, MaTacGia) VALUES (N'Gã hề ma quái', '105000', N'Gã hề ma quái là tiểu thuyết thuộc thể loại viễn tưởng kinh dị của nhà văn người Mỹ Stephen King. Đó là cuốn sách thứ 22 và cuốn tiểu thuyết thứ 17 của ông được viết dưới tên của chính ông.', 'it.jpg', '2022-10-24', '290', 3, 2, 3);
INSERT INTO Sach (TenSach, GiaBan, MoTa, AnhBia, NgayCapNhat, SoLuongTon, MaNXB, MaChuDe, MaTacGia) VALUES (N'The Shining', '90000', N'The Shining là một tiểu thuyết kinh dị năm 1977 của tác giả người Mỹ Stephen King. Đây là cuốn tiểu thuyết xuất bản thứ ba của King và là cuốn sách bán chạy đầu tiên trên bìa cứng; Thành công của nó đã giúp King trở thành một tác giả xuất sắc trong thể loại kinh dị.', 'theshining.jpg', '2022-10-24', '400', 3, 2, 3);
INSERT INTO Sach (TenSach, GiaBan, MoTa, AnhBia, NgayCapNhat, SoLuongTon, MaNXB, MaChuDe, MaTacGia) VALUES (N'Đối đầu', '90000', N'The Stand là một cuốn tiểu thuyết hậu tận thế kinh dị / tưởng tượng của tác giả người Mỹ Stephen King.', 'thestand.jpg', '2022-10-24', '450', 3, 2, 3);
INSERT INTO Sach (TenSach, GiaBan, MoTa, AnhBia, NgayCapNhat, SoLuongTon, MaNXB, MaChuDe, MaTacGia) VALUES (N'Khu vườn bí mật', '90000', N'Mary Lennox, một cô bé mồ côi, cáu kỉnh, không ai ưa, tới sống tại nhà ông bác ở vùng đồng hoang Yorkshire, nước Anh, nơi có rất nhiều bí mật đang đợi cô. Ban đêm, có tiếng khóc của ai đó từ một hành lang gần phòng cô. Còn ban ngày, cô gặp Dickon, một cậu bé biết thuần dưỡng và nói chuyện cùng loài vật. Rồi một ngày, nhờ sự giúp đỡ của một con chim ức đỏ, Mary khám phá ra một nơi bí ẩn nhất trên thế gian này – Khu vườn bí mật. Mọi thứ dường như đã chết trong khu vườn mười năm qua khóa kín. Cùng với Colin, vị chủ nhân ốm yếu của tiếng khóc bí ẩn kia, và Dickon, cậu bé mà tất cả mọi người đều yêu mến, Mary đã tìm ra những thứ sẽ làm thay đổi cuộc đời cô mãi mãi, khi mùa xuân về đánh thức cả khu vườn tuyệt đẹp.', 'secretGarden.jpg', '2022-10-24', 190, 3, 3, 4);
