INSERT INTO Sach (TenSach, GiaBan, MoTa, AnhBia, NgayCapNhat, SoLuongTon, MaNXB, MaChuDe, MaTacGia) VALUES (N'Hoàng tử bé', '90000', N'Hoàng tử bé, được xuất bản năm 1943, là tiểu thuyết nổi tiếng nhất của nhà văn và phi công Pháp 5 Saint-Exupéry. Ông đã thuê ngôi biệt thự The Bevin House ở Asharoken, Long Island, New York trong khi viết tác phẩm này. Cuốn tiểu thuyết cũng bao gồm nhiều bức tranh do chính Saint-Exupéry vẽ.', 'hoangtube.jpg', '2022-10-24', '123', 4, 3, 5);
INSERT INTO Sach (TenSach, GiaBan, MoTa, AnhBia, NgayCapNhat, SoLuongTon, MaNXB, MaChuDe, MaTacGia) VALUES (N'Đường mây qua xứ tuyết', '90000', N'The way of the white clouds (tạm dịch: Đường mây qua xứ tuyết) là tập sách ghi nhận những điều tác giả 6 chứng kiến trong thời gian du hành Tây Tạng. Đối với người Tây Tạng, mây có nhiều ý nghĩa huyền bí. Nhìn vào các bức họa Tây Tạng (thankas), gần như bức nào cũng thấy họ vẽ các đám mây màu sắc khác nhau. Mây tượng trưng cho sự sáng tạo vì nó có thể mang bất cứ hình thù gì. Mây trắng tượng trưng cho môi trường để sự sáng tạo có thể nẩy nở, phát sinh nhưng nó còn có nghĩa là đám mây Phá', 'quaxutuyet.jpg', '2022-10-24', 653, 4, 7, 6);
