INSERT INTO Sach (TenSach, GiaBan, MoTa, AnhBia, NgayCapNhat, SoLuongTon, MaNXB, MaChuDe, MaTacGia) VALUES (N'Firepunch', '300000', N'Được dịch từ tiếng Anh-Fire Punch là một bộ truyện tranh Nhật Bản được viết và minh họa bởi 8. Nó được đăng nhiều kỳ thông qua trang web Shōnen Jump + của Shueisha từ tháng 4 năm 2016 đến tháng 1 năm 2018, với các chương của nó được thu thập thành tám tập tankōbon.', 'firepunch.jpg', '2022-10-24', '324', 3, 5, 8);
INSERT INTO Sach (TenSach, GiaBan, MoTa, AnhBia, NgayCapNhat, SoLuongTon, MaNXB, MaChuDe, MaTacGia) VALUES (N'Goodbye Eri', '120000', N'Sayonara Eri là một manga one-shot Nhật Bản được viết và minh họa bởi Fujimoto Tatsuki. Truyện được phát hành trên website Shōnen Jump+ vào tháng 4 năm 2022 và được phát hành dưới dạng in vào tháng 7 năm 2022. ', 'goodbyeeri.jpg', '2022-10-24', 541, 3, 5, 8);
